updateDate = NOW(),
title = #{title},
`body`= #{body}

WHERE id = #{id}

UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}

